
-- INSERT MARZ BREWERY #7
INSERT INTO 
breweries (name, location) VALUES ('Marz Community Brewing Co.', '3630 South Iron Street, Chicago, IL 60608');

-- INSERT MARZ BEERS
INSERT INTO 
beers (beer_name, style, abv, description, brew_id) VALUES ('CITRA!CITRA!CITRA!', 'Double India Pale Ale', 8, 'Citra Citra Citra Simcoe Citra Citra Citra Centennial Citra Citra…', 7);
INSERT INTO 
beers (beer_name, style, abv, description, brew_id) VALUES ('Dank Grass', 'Gluten India Pale Ale', 5, 'Our gluten free IPA made with a mash of maize and Golden Naked Oats, with sorghum and jaggery added to the boil. Most of the hops are added to the whirlpool for this beer to limit bitterness, with a massive dry hopping for more hop flavor and aromas.', 7);
INSERT INTO 
beers (beer_name, style, abv, description, brew_id) VALUES ('Double Splash', 'Double India Pale Ale', 8, 'Fermented with the same yeast we use for Splash with a different fermentation schedule to keep the fruity esters while creating a sweeter finish.  Many of the hops in this beer are recent releases and add more tropical fruit flavors and aromas.', 7);
INSERT INTO 
beers (beer_name, style, abv, description, brew_id) VALUES ('Liquid Dreams', 'Double India Pale Ale', 9, 'Almost malty enough to qualify as an American Barleywine, and very well hopped with Mosaic and many other hops.  Named for our aspirations to open a professional brewery and produce beers people love to drink.', 7);
INSERT INTO 
beers (beer_name, style, abv, description, brew_id) VALUES ('Midway Chipa', 'Double India Pale Ale', 8, 'This is our spin on the hazy northeast double IPA style that generally feature huge hop aroma and flavors with a soft bitterness.  We use our own yeast blend to achieve the NE mouth feel with additional fruity esters and complexity.  Every batch of this beer is similar to the last, but with a different dry hop emphasized.  It is a way for us to explore new hops and keep the beer exciting.', 7);
INSERT INTO 
beers (beer_name, style, abv, description, brew_id) VALUES ('Lost Lake Piranhanas With Pineapple', 'American Pale Ale', 5.6, 'A Marz and Paul McGee collab, Piranhanas is fermented at high temps with a yeast blend that produces an array of tropical fruit flavors and dries out the beer, emphasizing the fermentation and hop flavors.', 7);
INSERT INTO 
beers (beer_name, style, abv, description, brew_id) VALUES ('Nasal Cavity', 'Stout', 5.5, 'A collaboration with Against the Grain. Contrary to popular belief, real wasabi root is not nearly as spicy as the green 'wasabi' blob provided at most sushi places — that is actually made with horseradish. In this beer, fresh wasabi root provides an immediate sensation and mouthfeel on the tongue, then changes to  tea-like flavors ', 7);
INSERT INTO 
beers (beer_name, style, abv, description, brew_id) VALUES ('The Machine', 'American Pale Ale', 5.3, 'With a nod to our hardworking and highly powerful political organizations here in Chicago, Marz made this delicious beer to buy your vote.', 7);
INSERT INTO 
beers (beer_name, style, abv, description, brew_id) VALUES ('The Bubbly Creek', 'Berliner Weiss', 3, 'The Bubbly Creek is the first brew in our South Side Sour line. Our Bubbly Creek is a traditional German style top–fermented wheat beer, made with both traditional warm–fermenting yeasts and our own strain of lactobacillus plantarum culture.', 7);
INSERT INTO 
beers (beer_name, style, abv, description, brew_id) VALUES ('Duchess De Bridgeport', 'Sour Red Ale', 6.3, 'A South Side Sour red ale brewed with our proprietary souring bacteria, Duchess De Bridgeport is our take on a Flanders Red. Notes of strawberry, dried fruit, and citric hops intermingle with a round lactic sourness. The Duchess is an homage to our mom, Maria Marszewski.', 7);
INSERT INTO 
beers (beer_name, style, abv, description, brew_id) VALUES ('What the Pho', 'Porter', 6.2, 'Inspired by the Vietnamese noodle soup Pho, this Marz — Urban Belly Brew is an experimental spiced porter. We use our Bridgeporter base beer and age it on oak and sassafrass wood staves and infused with Urban Belly soup Pho base. The beer is then dry hopped with a spice blend of coriander, cinnamon, star anise, clove.', 7);
INSERT INTO 
beers (beer_name, style, abv, description, brew_id) VALUES ('Lumen IPA', 'India Pale Ale', 6.2, 'Named after long running counter culture magazine Lumpen, we made this IPA a little different than most. There is plenty of body from caramel malts, a little roast from dark grains, and lots of Falconers Flight hop blend.', 7);